app.controller("ModalForgottenPassCtrl",["$scope","$modalInstance","auth",function(e,s,n){e.cancel=function(){s.dismiss("cancel")};e.ok=function(){if(e.search.username.length==0){return}n.forgotPass(e.search).then(function(n){if(n.data.success){s.close("Message was send to your email address!")}else{e.search.username="";alert(n.data.reason)}},function(e){console.log("something wrong: "+e)})}}]);