app.controller("ModalLoginCtrl",["$scope","$modalInstance","auth",function(e,n,r){e.clear=function(){document.getElementById("redAlert").innerHTML=""};e.forgottenPass=function(){n.close("s")};e.ok=function(){if(e.user){if(!e.user.username){document.getElementById("redAlert").innerHTML="Enter Username!";return}if(!e.user.password){document.getElementById("redAlert").innerHTML="Enter Password!";return}r.login(e.user).then(function(r){if(r.data.success){n.close(r.data.user)}else{e.user.username="";e.user.password="";document.getElementById("redAlert").innerHTML="Wrong username or password!"}},function(e){console.log("something wrong: "+e)})}};e.cancel=function(){n.dismiss("cancel")};e.signUp=function(){n.close("signUp")}}]);